function Home() {
  const { addToCart } = useCart();
  const [activeCategory, setActiveCategory] = useState('men');
  const [activeSubcategory, setActiveSubcategory] = useState('footwear');

  const handleCategoryChange = (category) => {
    setActiveCategory(category);
    // Reset subcategory to first available one when category changes
    const subcategories = Object.keys(products[category]);
    setActiveSubcategory(subcategories[0]);
  };

  return (
    <div className="home-container">
      <h2>Shop Our Collection</h2>
      
      <div className="category-tabs">
        {Object.keys(products).map(category => (
          <button
            key={category}
            className={activeCategory === category ? 'active' : ''}
            onClick={() => handleCategoryChange(category)}
          >
            {category.charAt(0).toUpperCase() + category.slice(1)}
          </button>
        ))}
      </div>
      
      <div className="subcategory-tabs">
        {Object.keys(products[activeCategory]).map(subcategory => (
          <button
            key={subcategory}
            className={activeSubcategory === subcategory ? 'active' : ''}
            onClick={() => setActiveSubcategory(subcategory)}
          >
            {subcategory.charAt(0).toUpperCase() + subcategory.slice(1)}
          </button>
        ))}
      </div>
      
      <div className="products">
        {products[activeCategory][activeSubcategory].map((product) => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.name} />
            <h4>{product.name}</h4>
            <p>₹{product.price.toLocaleString("en-IN")}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;